  @spec solve(grid :: [[integer]]) :: integer
  def solve(grid) do
    Agent.start_link(fn -> %{} end, name: __MODULE__)

    width = Enum.count(Enum.at(grid, 0))

    res = do_solve(grid, {0, 0, width - 1}, width)
    Agent.update(__MODULE__, fn _ -> %{} end)
    res
  end

  def do_solve([], {_, _, _}, _), do: 0
  def do_solve([row | rest], {r, col_1, col_2} = state, width) do
    cached_value = Agent.get(__MODULE__, &(Map.get(&1, state)))
    if cached_value do
      cached_value
    else
      res =
        for c1 <- [col_1 - 1, col_1, col_1 + 1],
            c2 <- [col_2 - 1, col_2, col_2 + 1],
            0 <= c1 and c1 < width,
            0 <= c2 and c2 < width,
            reduce: 0 do
          acc ->
            max(acc, do_solve(rest, {r + 1, c1, c2}, width))
        end
        |> Kernel.+(cost(row, state))

      Agent.update(__MODULE__, fn map ->
        map
        |> Map.put({r, col_1, col_2}, res)
        |> Map.put({r, col_2, col_1}, res)
      end)
      res
    end
  end

  def cost(row, {_, col, col}) do
    Enum.at(row, col)
  end
  def cost(row, {_, col_1, col_2}) do
    Enum.at(row, col_1) + Enum.at(row, col_2)
  end
