defmodule FindAnagrams do
  @doc """
      iex> FindAnagrams.solve("cbaebabacd", "abc")
      [0,6]

      iex> FindAnagrams.solve("abab", "ab")
      [0,1,2]

      iex> FindAnagrams.solve("ababababab", "aab")
      [0,2,4,6]

      iex> FindAnagrams.solve("", "")
      [0, 10001]
  """
  @spec solve(s :: String.t, p :: String.t) :: [integer]
  def solve(s, p) do
    window = String.length(p)

    if window > String.length(s) do
      []
    else
      frequencies =
        p
        |> String.to_charlist()
        |> Enum.frequencies()

      s
      |> String.to_charlist()
      |> Stream.chunk_every(window, 1, :discard)
      |> Stream.with_index()
      |> Stream.filter(fn {sub_array, _} ->
        Enum.frequencies(sub_array) == frequencies
      end)
      |> Stream.map(&elem(&1, 1))
      |> Enum.to_list()
    end
  end
end
